import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Header from '../components/common/Header';
import { useAtom } from 'jotai';
import { userAtom } from '../components/user/Login';
import { supabase } from '../services/supabase/supabase';
import { UserType } from '../types/supabase';

import UserPosts from '../components/mypage/UserPosts';

const EditProfile = () => {
  const [user] = useAtom(userAtom);
  const [isEditing, setIsEditing] = useState(false);
  const [nickname, setNickname] = useState('');
  const [selectedImage, setSelectedImage] = useState<File | null>(null);
  const [userData, setUserData] = useState<UserType | null>(null);

  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        const { data, error } = await supabase.from('users').select('*').eq('email', user.email);
        if (error) {
          console.error('Error fetching user data:', error);
        } else {
          setUserData(data[0]);
          setNickname(data[0].nickname);
        }
      }
    };

    fetchUserData();
  }, [user]);

  const handleEditClick = () => {
    setIsEditing(true);
    setNickname(userData?.nickname || '');
  };

  const handleNicknameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setNickname(event.target.value);
  };

  const handleSaveClick = async () => {
    try {
      if (user) {
        await supabase.from('users').update({ nickname }).eq('uid', user.uid);
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Error updating user information:', error);
    }
  };

  const handleImageChangeWrapper = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFiles = e.target.files;

    if (selectedFiles && selectedFiles.length > 0) {
      const updatedSelectedImage = selectedFiles[0]; // Use the first selected file
      setSelectedImage(updatedSelectedImage);
    }
  };
  const handleImageUpload = async () => {
    try {
      if (user && selectedImage) {
        const filePath = `profile_images/${user.uid}/${selectedImage.name}`;
        const uploadResponse = await supabase.storage.from(filePath).upload(filePath, selectedImage);

        if (uploadResponse.error) {
          console.error('프로필 이미지 업로드 오류:', uploadResponse.error);
        } else {
          const downloadURL = `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${filePath}`;

          await supabase.from('users').update({ profileimg: downloadURL }).eq('uid', user.uid);

          const { data, error } = await supabase.from('users').select('*').eq('email', user.email);
          if (error) {
            console.error('사용자 데이터 가져오기 오류:', error);
          } else {
            setUserData(data[0]);
          }
        }
      }
    } catch (error) {
      console.error('프로필 이미지 업로드 오류:', error);
    }
  };

  return (
    <div>
      <Link to="/">Home</Link>
      <Header />

      {user ? (
        <div>
          <h1>마이 페이지</h1>
          <div>
            <img
              src={userData?.profileimg ? `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${userData.profileimg}` : '-'}
              alt={`프로필 이미지 - ${user.uid}`}
              style={{
                width: 200,
                height: 200,
                borderRadius: 70,
                border: '3px solid black'
              }}
            />

            <button className="material-symbols-outlined" onClick={handleEditClick}>
              edit
            </button>

            <p>이메일: {userData ? userData.email : ''}</p>
            <p>닉네임: {userData ? userData.nickname : ''}</p>
          </div>
          {isEditing && (
            <div>
              <input type="text" value={nickname} onChange={handleNicknameChange} />
              <button onClick={handleSaveClick}>Save Nickname</button>
              <input type="file" accept="image/*" onChange={handleImageChangeWrapper} />
              <button onClick={handleImageUpload}>Upload Profile Image</button>
            </div>
          )}
        </div>
      ) : (
        <div>
          <h1>마이 페이지</h1>
          <p>Loading user data...</p>
        </div>
      )}
    </div>
  );
};

export default EditProfile;
