// Home.tsx

import React, { useState } from 'react';
import Banner from '../components/common/Banner';
import GetPost from '../components/posts/GetPost';

const Home = () => {
  const [searchKeyword, setSearchKeyword] = useState<string>(''); // 검색 키워드 상태 추가

  const handleSearch = (keyword: string) => {
    setSearchKeyword(keyword); // 검색 키워드 업데이트
  };

  return (
    <div>
      <Banner />
      {/* GetPost에 검색 키워드와 검색 핸들러 전달 */}
      <GetPost searchKeyword={searchKeyword} handleSearch={handleSearch} />
      {/* <InfiniteScroll /> */}
    </div>
  );
};

export default Home;
