import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import UserPosts from '../components/mypage/UserPosts';
import Header from '../components/common/Header';
import { useAtom } from 'jotai';
import { userAtom } from '../components/user/Login';
import { supabase } from '../services/supabase/supabase';
import { UserType } from '../types/supabase';

const Mypage = () => {
  const [user] = useAtom(userAtom);
  const [isEditing, setIsEditing] = useState(false);
  const [editedNickname, setEditedNickname] = useState('');
  const [, setSelectedImage] = useState<File | null>(null);

  const imageInputRef = useRef<HTMLInputElement | null>(null);
  const [userData, setUserData] = useState<UserType | null>(null);

  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        const { data, error } = await supabase.from('users').select('*').eq('email', user.email);
        if (error) {
          console.error('Error fetching user data:', error);
        } else {
          setUserData(data[0]);
          setEditedNickname(data[0].nickname);
        }
      }
    };

    fetchUserData();
  }, [user]);

  const handleImageClick = () => {
    if (imageInputRef.current) {
      imageInputRef.current.click();
    }
  };

  const handleImageChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedImage(event.target.files[0]);
    }
  };

  const handleNicknameChange = (event: React.ChangeEvent<HTMLInputElement>) => setEditedNickname(event.target.value);

  // 닉네임 변경
  const saveChanges = async () => {
    try {
      if (user) {
        await supabase.from('users').update({ nickname: editedNickname }).eq('uid', user.uid);
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Error updating user information:', error);
    }
  };

  return (
    <div>
      {/* 홈으로 이동 */}
      <Link to="/">Home</Link>
      <Header />

      {user ? (
        <div>
          <h1>마이 페이지</h1>
          <div>
            <img
              src={userData?.profileimg ? `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${userData.profileimg}` : '-'}
              alt={`프로필 이미지 - ${user.uid}`}
              style={{
                width: 200,
                height: 200,
                borderRadius: 70,
                cursor: 'pointer',
                border: '3px solid black'
              }}
            />

            <input
              type="file"
              accept="image/*"
              ref={imageInputRef}
              onChange={handleImageChange}
              style={{ display: 'none' }}
            />
            {/* 수정 버튼 */}
            <button className="material-symbols-outlined" onClick={() => setIsEditing(!isEditing)}>
              edit
            </button>

            <p>이메일: {userData ? userData.email : ''}</p>
            <p>닉네임: {userData ? userData.nickname : ''}</p>
          </div>
          {/* 수정 버튼 클릭시 입력폼 */}
          {isEditing && (
            <>
              <span>닉네임 입력:</span>
              <input type="text" value={editedNickname} onChange={handleNicknameChange} />
              <p />
              <button onClick={saveChanges}>Save</button>
              <p />
            </>
          )}
          {/* 유저 포스팅 */}
          <div>
            <UserPosts />
          </div>
        </div>
      ) : (
        <div>
          <h1>마이 페이지</h1>
          <p>Loading user data...</p>
        </div>
      )}
    </div>
  );
};

export default Mypage;
