import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Editor from '../editor/Editor';
import { v4 as uuidv4 } from 'uuid';

import { supabase } from '../../services/supabase/supabase';

const Post = () => {
  const navigate = useNavigate();
  const [newTitle, setNewTitle] = useState('');
  const [newBody, setNewBody] = useState('');
  const [selectedImages, setSelectedImages] = useState<File[]>([]);

  const handleAddPost = async () => {
    if (!newTitle.trim() || !newBody.trim()) {
      alert('제목과 본문을 모두 입력해주세요.');
      return;
    }

    const imageUrls = [];

    for (const selectedImage of selectedImages) {
      const existingImage = await supabase.storage.from('1st').getMetadata(`images/${selectedImage.name}`);

      if (existingImage.data) {
        imageUrls.push(existingImage.data.public_url); // Use the existing image's public URL
      } else {
        const { data, error } = await supabase.storage
          .from('1st')
          .upload(`images/${selectedImage.name}`, selectedImage);

        if (error) {
          console.error('Error uploading image to Supabase storage:', error);
          alert('이미지 업로드 중 에러가 발생했습니다!');
          return;
        }

        imageUrls.push(data.path);
      }
    }

    const { error: insertError } = await supabase
      .from('posts')
      .insert([{ title: newTitle, body: newBody, image_urls: imageUrls }]);

    if (insertError) {
      console.error('Error adding post:', insertError);
      alert('에러가 발생했습니다!');
      return;
    }

    setNewTitle('');
    setNewBody('');
    setSelectedImages([]);
    alert('글 작성이 완료되었습니다.');
    navigate(`/`);
  };

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFiles = Array.from(e.target.files || []);

    const newSelectedImages = selectedFiles.map((selectedFile) => {
      const originalFileName = selectedFile.name;
      const fileExtension = originalFileName.split('.').pop();
      const isEnglishFileName = /^[a-zA-Z0-9]+$/.test(originalFileName);

      const randomFileName = isEnglishFileName ? uuidv4() + '.' + fileExtension : originalFileName;

      return new File([selectedFile], randomFileName);
    });

    setSelectedImages((prevSelectedImages) => [...prevSelectedImages, ...newSelectedImages]);
  };

  return (
    <div>
      <div>
        <input type="text" placeholder="Title" value={newTitle} onChange={(e) => setNewTitle(e.target.value)} />
        <Editor value={newBody} onChange={(content) => setNewBody(content)} />

        <br />
        <br />
        <br />
        <br />
        <input type="file" accept="image/*" multiple onChange={handleImageChange} />
        <button onClick={handleAddPost}>글 작성하기</button>
      </div>
    </div>
  );
};

export default Post;
