import React, { useEffect, useState, useRef } from 'react';
import { NavLink } from 'react-router-dom';

import * as S from './Styled.Posts';
import { Post } from '../../types/supabase';
import { supabase } from '../../services/supabase/supabase';
import { filterdcategories } from '../category/Category';

export const GetPost = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [filteredPosts, setFilteredPosts] = useState<Post[]>([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const loadMoreRef = useRef<HTMLDivElement | null>(null);

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);

      const { data, error } = await supabase
        .from('posts')
        .select('*')
        .limit(10)
        .offset((page - 1) * 10);

      if (error) {
        console.error('Error fetching posts:', error);
      } else {
        const postsWithCompleteURLs = data.map((post: Post) => ({
          ...post,
          image_urls: post.image_urls ? post.image_urls.replace(/\[|\]|"/g, '').split(',') : []
        }));

        setPosts((prevPosts) => [...prevPosts, ...postsWithCompleteURLs]);
        setFilteredPosts((prevFilteredPosts) => [...prevFilteredPosts, ...postsWithCompleteURLs]);
        setLoading(false);
      }
    };

    fetchPosts();
  }, [page]);

  useEffect(() => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 1.0
    };

    const observer = new IntersectionObserver((entries) => {
      if (entries[0].isIntersecting && !loading) {
        setPage((prevPage) => prevPage + 1);
      }
    }, options);

    if (loadMoreRef.current) {
      observer.observe(loadMoreRef.current);
    }

    return () => {
      if (loadMoreRef.current) {
        observer.unobserve(loadMoreRef.current);
      }
    };
  }, [loading]);

  const handleCategoryClick = (category: string) => {
    if (category === '전체') {
      setFilteredPosts(posts);
    } else {
      const filteredPosts = posts.filter((post) => post.category === category);
      setFilteredPosts(filteredPosts);
    }
  };

  const categoryButtons = filterdcategories.map((category) => (
    <button key={category.value} value={category.value} onClick={() => handleCategoryClick(category.value)}>
      {category.label}
    </button>
  ));

  return (
    <>
      <div>{categoryButtons}</div>
      <S.PostContainer>
        {filteredPosts.map((post) => (
          <NavLink to={`/post/${post.pid}`} key={post.pid} style={{ textDecoration: 'none', color: 'inherit' }}>
            <S.PostItem>
              <h2>{post.title}</h2>
              <div>
                {post.image_urls.length > 0 && (
                  <S.Image
                    src={`${process.env.REACT_APP_SUPABASE_STORAGE_URL}${post.image_urls[0]}`}
                    alt={post.title}
                  />
                )}
              </div>
            </S.PostItem>
          </NavLink>
        ))}
        <div ref={loadMoreRef} style={{ height: '10px' }} />
      </S.PostContainer>
    </>
  );
};

export default GetPost;
