import React, { useEffect } from 'react';
import { RequestPayParams, RequestPayResponse } from 'iamport-typings';
import { ProductInfo } from '../../types/supabase';

interface IamPortProps {
  post: ProductInfo;
  handlePayment: (productInfo: ProductInfo, pg: string) => void;
}

const IamPort = ({ post, handlePayment }: IamPortProps) => {
  useEffect(() => {
    const IMP = window.IMP;
    const impKey = process.env.REACT_APP_IMP_KEY;

    if (impKey) {
      IMP?.init(impKey);
    } else {
      console.error('REACT_APP_IMP_KEY is not defined in the environment variables.');
    }
  }, []);

  return (
    <div>
      <button onClick={() => handlePayment(post, 'kakaopay')}>카카오페이 결제</button>
      <button onClick={() => handlePayment(post, 'inicis')}>이니시스 결제</button>
      <button onClick={() => handlePayment(post, 'tosspay')}>토스페이 결제</button>
    </div>
  );
};

export default IamPort;
