import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAtom } from 'jotai';
import { userAtom } from '../components/user/Login';
import { supabase } from '../services/supabase/supabase';
import { UserType } from '../types/supabase';

const EditProfile = () => {
  const [user] = useAtom(userAtom);
  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);
  const [editedNickname, setEditedNickname] = useState('');
  const [, setSelectedImage] = useState<File | null>(null);
  const [userData, setUserData] = useState<UserType | null>(null);

  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        const { data, error } = await supabase.from('users').select('*').eq('email', user.email);
        if (error) {
          console.error('Error fetching user data:', error);
        } else {
          setUserData(data[0]);
          setEditedNickname(data[0].nickname);
        }
      }
    };

    fetchUserData();
  }, [user]);

  const handleImageChangeWrapper = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files.length > 0) {
      setSelectedImage(event.target.files[0]);
    }
  };

  const handleProfileImageUpload = async () => {
    try {
      if (user && selectedImage) {
        const filePath = `profile_images/${user.uid}/${selectedImage.name}`;
        const uploadResponse = await supabase.storage.from(filePath).upload(filePath, selectedImage);

        if (uploadResponse.error) {
          console.error('프로필 이미지 업로드 오류:', uploadResponse.error);
        } else {
          const downloadURL = `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${filePath}`;
          await supabase.from('users').update({ profileimg: downloadURL }).eq('uid', user.uid);
          const { data, error } = await supabase.from('users').select('*').eq('email', user.email);
          if (error) {
            console.error('사용자 데이터 가져오기 오류:', error);
          } else {
            setUserData(data[0]);
          }
        }
      }
    } catch (error) {
      console.error('프로필 이미지 업로드 오류:', error);
    }
  };

  const handleNicknameChange = (event: React.ChangeEvent<HTMLInputElement>) => setEditedNickname(event.target.value);

  const saveChanges = async () => {
    try {
      if (user) {
        await supabase.from('users').update({ nickname: editedNickname }).eq('uid', user.uid);
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Error updating user information:', error);
    }
  };

  return (
    <div>
      <h1>프로필 수정</h1>
      <div>
        <img
          src={userData?.profileimg ? `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${userData.profileimg}` : '-'}
          alt={`프로필 이미지 - ${user?.uid}`}
          style={{
            width: 200,
            height: 200,
            borderRadius: 70,
            border: '3px solid black'
          }}
        />
        <input type="file" accept="image/*" onChange={handleImageChangeWrapper} />
        <button onClick={handleProfileImageUpload}>프로필 이미지 업로드</button>
      </div>
      <div>
        <p>이메일: {userData ? userData.email : ''}</p>
        {isEditing ? (
          <div>
            <input type="text" value={editedNickname} onChange={handleNicknameChange} />
            <button onClick={saveChanges}>저장</button>
          </div>
        ) : (
          <div>
            <p>닉네임: {userData ? userData.nickname : ''}</p>
            <button onClick={() => setIsEditing(true)}>수정</button>
          </div>
        )}
      </div>
      <button onClick={() => navigate(-1)}>뒤로 가기</button>
    </div>
  );
};

export default EditProfile;
