import React from 'react';
import { createClient } from '@supabase/supabase-js';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';
import Editor from '../editor/Editor';

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL as string;
const supabaseKey = process.env.REACT_APP_SUPABASE_KEY as string;

const supabase = createClient(supabaseUrl, supabaseKey);

interface Post {
  pid: number;
  title: string;
  body: string;
}

const Post = () => {
  const navigate = useNavigate();
  const [newTitle, setNewTitle] = useState('');
  const [newbody, setNewBody] = useState('');
  const [selectedImage, setSelectedImage] = useState(null); // 선택한 이미지 파일을 저장할 상태

  const handleAddPost = async () => {
    if (!newTitle.trim() || !newBody.trim()) {
      alert('제목과 본문을 모두 입력해주세요.');
      return;
    }

    if (selectedImage) {
      const formData = new FormData();
      formData.append('file', selectedImage);
      formData.append('upload_preset', 'your-cloudinary-upload-preset'); // Cloudinary의 업로드 프리셋 설정

      const response = await fetch('https://api.cloudinary.com/v1_1/your-cloudinary-cloud-name/image/upload', {
        method: 'POST',
        body: formData
      });

      if (!response.ok) {
        console.error('Error uploading image to Cloudinary.');
        alert('이미지 업로드 중 에러가 발생했습니다!');
        return;
      }

      const imageUploadResult = await response.json();
      const imageUrl = imageUploadResult.secure_url;

      const { data, error } = await supabase
        .from('posts')
        .insert([{ title: newTitle, body: newBody, image_url: imageUrl }]);
      if (error) {
        console.error('Error adding post:', error);
        alert('에러가 발생했습니다!');
        return;
      }
    }

    setNewTitle('');
    setNewBody('');
    setSelectedImage(null);
    alert('글 작성이 완료되었습니다.');
    navigate(`/`);
  };

  return (
    <div>
      <div>
        <input type="text" placeholder="Title" value={newTitle} onChange={(e) => setNewTitle(e.target.value)} />
        <Editor value={newbody} onChange={(content) => setNewBody(content)} />
        <br />
        <br />
        <br />
        <br />
        <button onClick={handleAddPost}>글 작성하기</button>
      </div>
    </div>
  );
};

export default Post;
