// EditPost.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { createClient } from '@supabase/supabase-js';
import Editor from '../editor/Editor';

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL as string;
const supabaseKey = process.env.REACT_APP_SUPABASE_KEY as string;

const supabase = createClient(supabaseUrl, supabaseKey);

interface Post {
  pid: number;
  title: string;
  body: string;
}

const EditPost = () => {
  const { pid } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState<Post | null>(null);

  useEffect(() => {
    async function fetchPost() {
      const { data: posts, error } = await supabase.from('posts').select('*').eq('pid', pid).single();
      if (error) {
        console.error('Error fetching post:', error);
      } else {
        setPost(posts);
      }
    }

    fetchPost();
  }, [id]);

  const [newTitle, setNewTitle] = useState('');
  const [newBody, setNewBody] = useState('');

  useEffect(() => {
    if (post) {
      setNewTitle(post.title);
      setNewBody(post.body);
    }
  }, [post]);

  const handleEditPost = async () => {
    if (!newTitle.trim() || !newBody.trim()) {
      alert('제목과 본문을 모두 입력해주세요.');
      return;
    }

    if (post && newTitle && newBody) {
      const { error } = await supabase.from('posts').update({ title: newTitle, body: newBody }).eq('id', post.id);

      if (error) {
        console.error('Error editing post:', error);
        alert('에러가 발생했습니다!');
      } else {
        alert('글 수정이 완료되었습니다.');
        navigate(`/`);
      }
    }
  };

  if (!post) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <input type="text" placeholder="Title" value={newTitle} onChange={(e) => setNewTitle(e.target.value)} />
      <Editor value={newBody} onChange={(content) => setNewBody(content)} />
      <br />
      <br />
      <br />
      <br />
      <button onClick={handleEditPost}>수정하기</button>
    </div>
  );
};

export default EditPost;
