import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';

import * as S from './Styled.Posts';
import { Post } from '../../types/supabase';
import { supabase } from '../../services/supabase/supabase';

const GetPost = () => {
  const [posts, setPosts] = useState<Post[]>([]);

  const supabaseStorageUrl = process.env.REACT_APP_SUPABASE_STORAGE_URL;

  useEffect(() => {
    async function fetchPosts() {
      const { data, error } = await supabase.from('posts').select('*');

      if (error) {
        console.error('Error fetching posts:', error);
      } else {
        setPosts(data);
      }
    }

    fetchPosts();
  }, []);

  return (
    <S.PostContainer>
      {posts.map((post) => (
        <NavLink to={`/post/${post.pid}`} key={post.pid} style={{ textDecoration: 'none', color: 'inherit' }}>
          <S.PostItem>
            <h2>{post.title}</h2>
            <div>
              {/* Assuming the post.image_urls is an array */}
              {Array.isArray(post.image_urls) ? (
                post.image_urls.map((imageUrl, index) => (
                  <S.Image key={index} src={`${supabaseStorageUrl}${imageUrl}`} alt={post.title} />
                ))
              ) : (
                <S.Image src={`${supabaseStorageUrl}${post.image_urls}`} alt={post.title} />
              )}
            </div>
          </S.PostItem>
        </NavLink>
      ))}
    </S.PostContainer>
  );
};

export default GetPost;
