import { useAtom } from 'jotai';
import { searchKeywordAtom } from './searchKeywordAtom'; // 앞서 정의한 Jotai 상태를 불러옴

const Search = () => {
  const [searchKeyword, setSearchKeyword] = useAtom(searchKeywordAtom); // Jotai의 검색 키워드 상태 사용

  const handleSearchClick = () => {
    // 검색어 상태 업데이트
    setSearchKeyword(searchKeyword);
  };

  return (
    <div>
      <input
        type="text"
        value={searchKeyword}
        onChange={(e) => setSearchKeyword(e.target.value)}
        placeholder="검색어를 입력하세요"
      />
      <button onClick={handleSearchClick}>검색</button>
    </div>
  );
};

export default Search;
