import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // 슬라이드 스타일 CSS
import Comments from '../components/comments/Comments';

import * as S from '../components/posts/Styled.Posts';
import { Post } from '../types/supabase';
import { supabase } from '../services/supabase/supabase';

const Detail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState<Post | null>(null);

  useEffect(() => {
    const fetchPost = async () => {
      const { data: posts, error } = await supabase.from('posts').select('*').eq('pid', id).single();
      if (error) {
        console.error('Error fetching post:', error);
      } else {
        const parsedImageUrls = JSON.parse(posts.image_urls);

        const detailedPost: Post = {
          ...posts,
          image_urls: parsedImageUrls
        };

        setPost(detailedPost);
      }
    };

    fetchPost();
  }, [id]);

  const handleEdit = () => {
    navigate(`/editpost/${post?.pid}`);
  };

  const handleDelete = async () => {
    const { error } = await supabase.from('posts').delete().eq('pid', post?.pid);

    if (error) {
      console.error('Error deleting post:', error);
    } else {
      alert('삭제 완료!');
      navigate(`/`);
    }
  };

  if (!post) {
    return <div>Loading...</div>;
  }

  return (
    <div style={{ width: '1200px', height: '800px', margin: '0 auto', overflow: 'hidden' }}>
      <div style={{ display: 'flex' }}>
        <div style={{ width: '250px', marginLeft: '250px', marginRight: '20px' }}>
          <Carousel>
            {post.image_urls.map((imageUrl, index) => (
              <div key={index} style={{ display: 'flex', alignItems: 'center', height: '350px' }}>
                <S.Image
                  src={`${process.env.REACT_APP_SUPABASE_STORAGE_URL}${imageUrl}`}
                  alt={`Image ${index}`}
                  style={{ maxWidth: '100%', maxHeight: '100%' }}
                />
              </div>
            ))}
          </Carousel>

          <div className="Contents-Container">
            <h2>{post.title}</h2>
            <p>{post.price}</p>
          </div>
        </div>
        <div>
          <div dangerouslySetInnerHTML={{ __html: post.body }} />
          <br />
          <button onClick={handleEdit}>수정하기</button>
          <button onClick={handleDelete}>삭제하기</button>
        </div>
      </div>
      <Comments />
    </div>
  );
};

export default Detail;
