import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';

import { Post } from '../../types/supabase';
import { supabase } from '../../services/supabase/supabase';

const GetPost = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [firstImage, setFirstImage] = useState<string | null>(null);

  useEffect(() => {
    async function fetchPosts() {
      const { data, error } = await supabase.from('posts').select('*');

      if (error) {
        console.error('Error fetching posts:', error);
      } else {
        const postsWithCompleteURLs = data.map((post) => ({
          ...post,
          image_url: `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${post.image_urls}`
        }));

        setPosts(postsWithCompleteURLs);

        if (postsWithCompleteURLs.length > 0) {
          setFirstImage(postsWithCompleteURLs[0].image_url);
        }
      }
    }

    fetchPosts();
  }, []);

  return (
    <PostContainer>
      {firstImage && (
        <NavLink to={`/post/${posts[0].pid}`} style={{ textDecoration: 'none', color: 'inherit' }}>
          <PostItem>
            <h2>{posts[0].title}</h2>
            <div>
              <img src={firstImage} alt={posts[0].title} />
            </div>
          </PostItem>
        </NavLink>
      )}
    </PostContainer>
  );
};

export default GetPost;

const PostContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(5, 1fr);
  gap: 10px;
`;

const PostItem = styled.div`
  border: 1px solid #ddd;
  padding: 10px;
  width: 150px;
  height: 300px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  overflow: hidden;
`;
