import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import Editor from '../editor/Editor';
import { v4 as uuidv4 } from 'uuid';

import { supabase } from '../../services/supabase/supabase';

const EditPost = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editBody, setEditBody] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    async function fetchPost() {
      const { data, error } = await supabase.from('posts').select('*').eq('pid', id).single();
      if (error) {
        console.error('Error fetching post:', error);
      } else {
        setPost(data);
        setEditTitle(data.title);
        setEditBody(data.body);
      }
    }

    fetchPost();
  }, [id]);

  const handleEditPost = async () => {
    if (!editTitle.trim() || !editBody.trim()) {
      alert('제목과 본문을 모두 입력해주세요.');
      return;
    }

    let updatedImageUrl = post.image_urls;

    if (selectedImage) {
      const { data, error } = await supabase.storage.from('1st').upload(`images/${selectedImage.name}`, selectedImage);

      if (error) {
        console.error('Error uploading image to Supabase storage:', error);
        alert('이미지 업로드 중 에러가 발생했습니다!');
        return;
      }

      updatedImageUrl = [data.path];
    }

    const { error } = await supabase
      .from('posts')
      .update({ title: editTitle, body: editBody, image_urls: updatedImageUrl })
      .eq('pid', post.pid);

    if (error) {
      console.error('Error editing post:', error);
      alert('에러가 발생했습니다!');
    } else {
      alert('글 수정이 완료되었습니다.');
      navigate(-1);
    }
  };

  if (!post) {
    return <div>Loading...</div>;
  }

  const handleImageChange = (e) => {
    const selectedFile = e.target.files && e.target.files[0];
    if (selectedFile) {
      const originalFileName = selectedFile.name;
      const fileExtension = originalFileName.split('.').pop();
      const randomFileName = uuidv4() + '.' + fileExtension;
      setSelectedImage(new File([selectedFile], randomFileName));
    }
  };

  return (
    <div>
      <input type="text" placeholder="Title" value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
      <Editor value={editBody} onChange={(content) => setEditBody(content)} />
      <br />
      <br />
      <br />
      <br />
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleEditPost}>수정하기</button>
    </div>
  );
};

export default EditPost;
