import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import { useAtom } from 'jotai';

import * as S from './Styled.Posts';
import { Post } from '../../types/supabase';
import { supabase } from '../../services/supabase/supabase';
import { filterdcategories } from '../category/Category';

export const GetPost = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [filteredPosts, setFilteredPosts] = useState<Post[]>([]);
  const [searchKeyword] = useAtom(searchKeywordAtom);

  useEffect(() => {
    const fetchPosts = async () => {
      const { data, error } = await supabase.from('posts').select('*');

      if (error) {
        console.error('Error fetching posts:', error);
      } else {
        const postsWithCompleteURLs = data.map((post) => ({
          ...post,
          image_urls: post.image_urls ? post.image_urls.replace(/\[|\]|"/g, '').split(',') : []
        }));

        setPosts(postsWithCompleteURLs);
        setFilteredPosts(postsWithCompleteURLs);
      }
    };

    fetchPosts();
  }, []);

  useEffect(() => {
    if (searchKeyword.trim() === '') {
      // 검색 키워드가 없으면 전체 포스트를 보여줌
      setFilteredPosts(posts);
    } else {
      // 검색 키워드가 있을 때, 제목에 키워드가 포함된 포스트를 필터링하여 보여줌
      const filteredPosts = posts.filter((post) => post.title.toLowerCase().includes(searchKeyword.toLowerCase()));
      setFilteredPosts(filteredPosts);
    }
  }, [searchKeyword, posts]);

  const handleCategoryClick = (category: string) => {
    if (category === '전체') {
      setFilteredPosts(posts);
    } else {
      const filteredPosts = posts.filter((post) => post.category === category);
      setFilteredPosts(filteredPosts);
    }
  };

  const categoryButtons = filterdcategories.map((category) => (
    <button key={category.value} value={category.value} onClick={() => handleCategoryClick(category.value)}>
      {category.label}
    </button>
  ));

  return (
    <>
      <div>{categoryButtons}</div>
      <S.PostContainer>
        {filteredPosts.map((post) => (
          <NavLink to={`/post/${post.pid}`} key={post.pid} style={{ textDecoration: 'none', color: 'inherit' }}>
            <S.PostItem>
              <h2>{post.title}</h2>
              <div>
                {post.image_urls.length > 0 && (
                  <S.Image
                    src={`${process.env.REACT_APP_SUPABASE_STORAGE_URL}${post.image_urls[0]} `}
                    alt={post.title}
                  />
                )}
              </div>
            </S.PostItem>
          </NavLink>
        ))}
      </S.PostContainer>
    </>
  );
};

export default GetPost;
