import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';

import { Post } from '../../types/supabase';
import { supabase } from '../../services/supabase/supabase';
import { categories } from '../category/Category';

import * as S from './Styled.Posts';

export const GetPost = () => {
  const [page, setPage] = useState<number>(1);
  const [pageSize] = useState<number>(10); // 한 번에 가져올 데이터 개수
  const [posts, setPosts] = useState<Post[]>([]);
  const [filteredPosts, setFilteredPosts] = useState<Post[]>([]);
  const [postEnd, setPostEnd] = useState<boolean>(false);

  const fetchPosts = async (pageNumber: number) => {
    const { data, error } = await supabase
      .from('posts')
      .select('*')
      .range((pageNumber - 1) * pageSize, pageNumber * pageSize - 1);

    console.log('data', data);

    if (error) {
      console.error('Error fetching posts:', error);
      return [];
    } else {
      const postsWithCompleteURLs = data.map((post: Post) => ({
        ...post,
        image_urls: post.image_urls || []
      }));

      // 중복된 데이터 필터링
      const filteredPosts = postsWithCompleteURLs.filter(
        (post) => !posts.some((prevPost) => prevPost.pid === post.pid)
      );

      return filteredPosts;
    }
  };

  const fetchNextPage = async () => {
    const nextPage = page + 1;
    const newPosts = await fetchPosts(nextPage);
    if (newPosts.length === 0) {
      setPostEnd(true);
    } else {
      setPage(nextPage);
      setFilteredPosts((prevFilteredPosts) => [...prevFilteredPosts, ...newPosts]);
    }
  };

  useEffect(() => {
    fetchPosts(page).then((newPosts) => {
      if (newPosts.length === 0) {
        setPostEnd(true);
      } else {
        setPosts((prevPosts) => [...prevPosts, ...newPosts]);
        setFilteredPosts((prevFilteredPosts) => [...prevFilteredPosts, ...newPosts]);

        if (newPosts.length < pageSize) {
          setPostEnd(true);
        }
      }
    });

    const handleScroll = () => {
      const { innerHeight } = window;
      const { scrollHeight, scrollTop } = document.documentElement;

      if (Math.round(scrollTop + innerHeight) >= scrollHeight && !postEnd) {
        fetchNextPage();
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [page, postEnd]);

  // const handleCategoryClick = (category: string) => {
  //   if (category === '전체') {
  //     setFilteredPosts(posts);
  //   } else {
  //     const filteredPosts = posts.filter((post) => post.category === category);
  //     setFilteredPosts(filteredPosts);
  //   }
  // };

  // const categoryButtons = categories.map((category) => (
  //   <button key={category.value} value={category.value} onClick={() => handleCategoryClick(category.value)}>
  //     {category.label}
  //   </button>
  // ));

  return (
    <>
      <div>{categoryButtons}</div>
      <S.PostContainer>
        {filteredPosts.map((post) => (
          <NavLink to={`/post/${post.pid}`} key={post.pid} style={{ textDecoration: 'none', color: 'inherit' }}>
            <S.PostItem>
              <h2>{post.title}</h2>
              <div>
                {post.image_urls.length > 0 && (
                  <S.Image
                    src={`${process.env.REACT_APP_SUPABASE_STORAGE_URL}${post.image_urls[0]} `}
                    alt={post.title}
                  />
                )}
              </div>
            </S.PostItem>
          </NavLink>
        ))}
      </S.PostContainer>
      {postEnd && <S.EndMassage>더 이상의 게시물이 없습니다.</S.EndMassage>}
    </>
  );
};

export default GetPost;
