// EditPost.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { createClient } from '@supabase/supabase-js';
import Editor from '../editor/Editor';

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL as string;
const supabaseKey = process.env.REACT_APP_SUPABASE_KEY as string;

const supabase = createClient(supabaseUrl, supabaseKey);

interface Post {
  pid: number;
  title: string;
  body: string;
  image_url: string;
}

const EditPost = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState<Post | null>(null);
  const [editTitle, setEditTitle] = useState('');
  const [editBody, setEditBody] = useState('');
  const [selectedImage, setSelectedImage] = useState<File | null>(null); // State for new selected image

  useEffect(() => {
    async function fetchPost() {
      const { data: posts, error } = await supabase.from('posts').select('*').eq('pid', id).single();
      if (error) {
        console.error('Error fetching post:', error);
      } else {
        setPost(posts);
        setEditTitle(posts.title);
        setEditBody(posts.body);
      }
    }

    fetchPost();
  }, []);

  const handleEditPost = async () => {
    if (!editTitle.trim() || !editBody.trim()) {
      alert('제목과 본문을 모두 입력해주세요.');
      return;
    }

    let updatedImageUrl = post?.image_url;

    if (selectedImage) {
      const fileName = selectedImage.name;

      // 파일 이름에 한글이나 특수문자가 있는 경우 처리
      const sanitizedFileName = fileName.replace(/[^\w\d.]/g, '_');

      // 이미지가 이미 존재하는 경우에 대한 처리
      const { data: existingImage } = await supabase.storage.from('1st').getPublicUrl(`images/${sanitizedFileName}`);

      if (!existingImage) {
        const { error } = await supabase.storage.from('1st').upload(`images/${sanitizedFileName}`, selectedImage);

        if (error) {
          console.error('Error uploading image to Supabase storage:', error);
          alert('이미지 업로드 중 에러가 발생했습니다!');
          return;
        }

        updatedImageUrl = `images/${sanitizedFileName}`; // 이미지 URL을 상대 경로로 업데이트
      } else {
        updatedImageUrl = `images/${sanitizedFileName}`; // 이미지 URL을 상대 경로로 업데이트
      }
    }

    if (post && editTitle && editBody) {
      const { error } = await supabase
        .from('posts')
        .update({ title: editTitle, body: editBody, image_url: updatedImageUrl })
        .eq('pid', post.pid);

      if (error) {
        console.error('Error editing post:', error);
        alert('에러가 발생했습니다!');
      } else {
        alert('글 수정이 완료되었습니다.');
        navigate(-1);
      }
    }
  };

  if (!post) {
    return <div>Loading...</div>;
  }

  const handleImageChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const selectedFile = e.target.files && e.target.files[0];
    if (selectedFile) {
      setSelectedImage(selectedFile);
    }
  };

  return (
    <div>
      <input type="text" placeholder="Title" value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
      <Editor value={editBody} onChange={(content) => setEditBody(content)} />
      <br />
      <br />
      <br />
      <br />
      <input type="file" accept="image/*" onChange={handleImageChange} />
      <button onClick={handleEditPost}>수정하기</button>
    </div>
  );
};

export default EditPost;
