import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // 슬라이드 스타일 CSS
import Comments from '../components/comments/Comments';

import * as S from '../components/posts/Styled.Posts';
import { Post } from '../types/supabase';
import { supabase } from '../services/supabase/supabase';

const Detail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState<Post | null>(null);

  useEffect(() => {
    const fetchPost = async () => {
      const { data: posts, error } = await supabase.from('posts').select('*').eq('pid', id).single();
      if (error) {
        console.error('Error fetching post:', error);
      } else {
        const parsedImageUrls = JSON.parse(posts.image_urls);

        const detailedPost: Post = {
          ...posts,
          image_urls: parsedImageUrls
        };

        setPost(detailedPost);
      }
    };

    fetchPost();
  }, [id]);

  const handleEdit = () => {
    navigate(`/editpost/${post?.pid}`);
  };

  const handleDelete = async () => {
    const { error } = await supabase.from('posts').delete().eq('pid', post?.pid);

    if (error) {
      console.error('Error deleting post:', error);
    } else {
      alert('삭제 완료!');
      navigate(`/`);
    }
  };

  if (!post) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div>
        <h2>{post.title}</h2>
        <Carousel
          showThumbs={false}
          showArrows={true}
          renderArrowPrev={(onClickHandler, hasPrev, label) =>
            hasPrev && (
              <button
                type="button"
                onClick={onClickHandler}
                title={label}
                style={{
                  left: '0', // Adjust the left position as needed
                  transform: 'translateY(-50%)',
                  zIndex: 1,
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  position: 'absolute',
                  top: '50%'
                }}
              >
                &lt; {/* Left arrow */}
              </button>
            )
          }
          renderArrowNext={(onClickHandler, hasNext, label) =>
            hasNext && (
              <button
                type="button"
                onClick={onClickHandler}
                title={label}
                style={{
                  right: '0', // Adjust the right position as needed
                  transform: 'translateY(-50%)',
                  zIndex: 1,
                  background: 'none',
                  border: 'none',
                  cursor: 'pointer',
                  position: 'absolute',
                  top: '50%'
                }}
              >
                &gt; {/* Right arrow */}
              </button>
            )
          }
        >
          {post.image_urls.map((imageUrl, index) => (
            <div key={index} style={{ position: 'relative' }}>
              <S.Image
                src={`${process.env.REACT_APP_SUPABASE_STORAGE_URL}${imageUrl}`}
                alt={`Image ${index}`}
                style={{ maxWidth: '250px', maxHeight: '250px', margin: '0 auto' }}
              />
            </div>
          ))}
        </Carousel>

        <br />
        <div dangerouslySetInnerHTML={{ __html: post.body }} />
        <br />
        <button onClick={handleEdit}>수정하기</button>
        <button onClick={handleDelete}>삭제하기</button>
      </div>
      <Comments />
    </>
  );
};

export default Detail;
