import React, { useEffect, useState } from 'react';
import { NavLink } from 'react-router-dom';
import { styled } from 'styled-components';
import { Post } from '../../types/supabase';
import { supabase } from '../../services/supabase/supabase';
import { categories } from '../category/Category';

import * as S from './Styled.Posts';

export const GetPost = () => {
  const [page, setPage] = useState<number>(1);
  const [posts, setPosts] = useState<Post[]>([]);
  const [filteredPosts, setFilteredPosts] = useState<Post[]>([]);
  const [postEnd, setPostEnd] = useState<boolean>(false);

  const fetchPostsAndPageUpdate = async () => {
    const { data, error } = await supabase.from('posts').select('*');

    if (error) {
      console.error('Error fetching posts:', error);
    } else {
      const postsWithCompleteURLs = data.map((post) => ({
        ...post,
        image_urls: post.image_urls ? post.image_urls.replace(/\[|\]|"/g, '').split(',') : []
      }));

      setPosts(postsWithCompleteURLs);
      setFilteredPosts(postsWithCompleteURLs);
      setPage((prevPage) => prevPage + 1); // 페이지 업데이트
    }
  };

  useEffect(() => {
    const handleScroll = () => {
      const { innerHeight } = window;
      const { scrollHeight } = document.body;
      const { scrollTop } = document.documentElement;
      if (Math.round(scrollTop + innerHeight) >= scrollHeight) {
        fetchPostsAndPageUpdate(); // 호출해서 새로운 데이터 가져오고 페이지 업데이트
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleCategoryClick = (category: string) => {
    if (category === '전체') {
      setFilteredPosts(posts);
    } else {
      const filteredPosts = posts.filter((post) => post.category === category);
      setFilteredPosts(filteredPosts);
    }
  };

  const categoryButtons = categories.map((category) => (
    <button key={category.value} value={category.value} onClick={() => handleCategoryClick(category.value)}>
      {category.label}
    </button>
  ));

  return (
    <>
      <div>{categoryButtons}</div>
      <Container>
        {filteredPosts.map((post) => (
          <NavLink to={`/post/${post.pid}`} key={post.pid} style={{ textDecoration: 'none', color: 'inherit' }}>
            <PostItem>
              <h2>{post.title}</h2>
              <div>
                {post.image_urls.length > 0 && (
                  <S.Image
                    src={`${process.env.REACT_APP_SUPABASE_STORAGE_URL}${post.image_urls[0]} `}
                    alt={post.title}
                  />
                )}
              </div>
            </PostItem>
          </NavLink>
        ))}
        {postEnd && <EndMessage>더 이상의 게시물이 없습니다.</EndMessage>}
      </Container>
    </>
  );
};

const Container = styled.div`
  /* 스타일 정의 */
`;

const PostItem = styled.div`
  /* 스타일 정의 */
`;

const EndMessage = styled.div`
  /* 스타일 정의 */
`;

export default GetPost;
