import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import UserPosts from '../components/mypage/UserPosts';
import Header from '../components/common/Header';
import { useAtom } from 'jotai';
import { userAtom } from '../components/user/Login';
import { supabase } from '../services/supabase/supabase';
import { UserType } from '../types/supabase';

const Mypage = () => {
  const [user] = useAtom(userAtom);
  const [isEditing, setIsEditing] = useState(false);
  const [nickname, setNickname] = useState('');

  const [userData, setUserData] = useState<UserType | null>(null);

  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        const { data, error } = await supabase.from('users').select('*').eq('email', user.email);
        if (error) {
          console.error('Error fetching user data:', error);
        } else {
          setUserData(data[0]);
          setNickname(data[0].nickname);
        }
      }
    };

    fetchUserData();
  }, [user]);

  const handleEditClick = () => {
    setIsEditing(true);
    setNickname(userData?.nickname || ''); // Set the current nickname to the state
  };

  const handleNicknameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setNickname(event.target.value);
  };

  const handleSaveClick = async () => {
    try {
      if (user) {
        await supabase.from('users').update({ nickname }).eq('uid', user.uid);
        setIsEditing(false);
      }
    } catch (error) {
      console.error('Error updating user information:', error);
    }
  };

  return (
    <div>
      {/* 홈으로 이동 */}
      <Link to="/">Home</Link>
      <Header />

      {user ? (
        <div>
          <h1>마이 페이지</h1>
          <div>
            <img
              src={userData?.profileimg ? `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${userData.profileimg}` : '-'}
              alt={`프로필 이미지 - ${user.uid}`}
              style={{
                width: 200,
                height: 200,
                borderRadius: 70,
                border: '3px solid black'
              }}
            />

            {/* 수정 버튼 */}
            <button className="material-symbols-outlined" onClick={handleEditClick}>
              edit
            </button>

            <p>이메일: {userData ? userData.email : ''}</p>
            <p>닉네임: {userData ? userData.nickname : ''}</p>
          </div>
          {/* 수정 버튼 클릭시 입력폼 */}
          {isEditing && (
            <div>
              <input type="text" value={nickname} onChange={handleNicknameChange} />
              <button onClick={handleSaveClick}>Save Nickname</button>
              {/* Add the file input for profile image upload */}
              <input type="file" accept="image/*" onChange={handleImageChange} />
              <button onClick={handleImageUpload}>Upload Profile Image</button>
            </div>
          )}
          {/* 유저 포스팅 */}
          <div>
            <UserPosts />
          </div>
        </div>
      ) : (
        <div>
          <h1>마이 페이지</h1>
          <p>Loading user data...</p>
        </div>
      )}
    </div>
  );
};

export default Mypage;
