import React from 'react';
import { atom, useAtom } from 'jotai';
import { styled } from 'styled-components';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { signUpService } from '../../services/supabase/auth';
import type { UserType } from '../../types/supabase';

type SignUpType = {
  setLoginModal: (isOpen: boolean) => void;
  setSignUpmodal: (isOpen: boolean) => void;
};

export const userDataAtom = atom<UserType>({
  uid: '',
  email: '',
  password: '',
  nickname: '',
  profileImg: '' // 프로필 이미지는 초기에 null로 설정
});

const SignUp = ({ setLoginModal, setSignUpmodal }: SignUpType) => {
  const queryClient = useQueryClient();
  const [userData, setUserData] = useAtom(userDataAtom);

  const signUpMutation = useMutation(signUpService, {
    onSuccess: async () => {
      queryClient.invalidateQueries({ queryKey: [userData] });
    }
  });

  const signUpHandler = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      // 이미지 업로드 및 URL 저장
      const uploadedImageUrl = await uploadImageAndSaveUser(userData);

      if (uploadedImageUrl) {
        // 사용자 데이터에 이미지 URL 추가
        const userDataWithImage = { ...userData, profileImg: uploadedImageUrl };

        // 회원가입 서비스 호출
        signUpMutation.mutate(userDataWithImage);
        alert('회원가입이 완료되었습니다!');
        setSignUpmodal(false);
        setLoginModal(true);
      } else {
        console.error('Failed to upload image and save user.');
      }
    } catch (error) {
      console.error(error);
    }
  };

  const closeSignUpModal = () => {
    setSignUpmodal(false);
  };

  return (
    <Container>
      <CloseBtn onClick={closeSignUpModal}>x</CloseBtn>
      <Wapper>
        <form onSubmit={signUpHandler}>
          {/* (이메일, 비밀번호, 닉네임 등 입력 필드) */}
          프로필 사진 :
          <input
            type="file"
            onChange={(e) => setUserData({ ...userData, profileImg: e.target.files[0] })}
            accept="image/*" // 이미지 파일만 선택 가능하도록
          ></input>
          <button>회원 가입 완료</button>
        </form>
      </Wapper>
    </Container>
  );
};

export default SignUp;

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  width: 300px;
  height: 400px;

  z-index: 999;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);

  background-color: gray;
  border: 1px solid black;
  border-radius: 8px;
`;

const Wapper = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const CloseBtn = styled.button`
  position: absolute;
  right: 10px;
  top: 10px;
`;
