import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import Comments from '../components/comments/Comments';

import { Post } from '../types/supabase';
import { supabase } from '../services/supabase/supabase';

const Detail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState<Post | null>(null);
  const [imageUrls, setImageUrls] = useState<string[]>([]); // State to store image URLs

  useEffect(() => {
    async function fetchPost() {
      const { data: posts, error } = await supabase.from('posts').select('*').eq('pid', id).single();
      if (error) {
        console.error('Error fetching post:', error);
      } else {
        setPost(posts);

        // Process image URLs if they exist
        if (posts.image_urls) {
          const urls = posts.image_urls.split(',').map((url) => `${process.env.REACT_APP_SUPABASE_STORAGE_URL}${url}`);
          setImageUrls(urls);
        }
      }
    }

    fetchPost();
  }, [id]);

  const handleEdit = () => {
    navigate(`/editpost/${post?.pid}`);
  };

  const handleDelete = async () => {
    const { error } = await supabase.from('posts').delete().eq('pid', post?.pid);

    if (error) {
      console.error('Error deleting post:', error);
    } else {
      alert('삭제 완료!');
      navigate(`/`);
    }
  };

  if (!post) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <div>
        <h2>{post.title}</h2>
        <br />
        {imageUrls.length > 0 && (
          <div>
            {imageUrls.map((url, index) => (
              <img key={index} src={url} alt={`Image ${index}`} />
            ))}
          </div>
        )}
        <br />
        <div dangerouslySetInnerHTML={{ __html: post.body }} />
        <br />
        <button onClick={handleEdit}>수정하기</button>
        <button onClick={handleDelete}>삭제하기</button>
      </div>
      <Comments />
    </>
  );
};

export default Detail;
