import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Post } from '../../types/supabase';
import { supabase } from '../../services/supabase/supabase';
import { useNavigate } from 'react-router-dom';
import * as S from './Styled.Pagination';

const Pagination = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [jjimData, setJjimData] = useState<{ pid: string }[]>([]); // jjim 테이블 데이터
  const [uid, setUid] = useState<string | null>(null);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      const userData = localStorage.getItem('jotaiUserData');
      if (userData) {
        const parsedUserData = JSON.parse(userData);
        setUid(parsedUserData.uid);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    const fetchJjimData = async () => {
      const { data: jjimData, error: jjimError } = await supabase.from('jjim').select('pid').eq('uid', uid);

      if (jjimError) {
        console.error('Error fetching jjim:', jjimError);
      } else {
        setJjimData(jjimData);
      }
    };

    fetchJjimData();
  }, [uid]);

  useEffect(() => {
    const fetchPosts = async () => {
      const { data, error } = await supabase.from('posts').select('*');

      if (error) {
        console.error('Error fetching posts:', error);
      } else {
        const postsWithCompleteURLs = data.map((post) => ({
          ...post,
          image_urls: post.image_urls ? post.image_urls.replace(/\[|\]|"/g, '').split(',') : []
        }));

        setPosts(postsWithCompleteURLs);
      }
    };

    fetchPosts();
  }, []);

  const jjimPosts = jjimData.map((jjim) => jjim.pid.toString()); // 해당 유저가 찜한 글의 pid 목록
  // 해당 유저가 찜한 글의 pid 목록
  const filteredPosts = posts.filter((post) => jjimPosts.includes(post.pid)); // 찜한 글만 필터링

  const pagePerObjects = 5; // 페이지 당 데이터 수
  const totalCount = filteredPosts.length;
  const totalPages = Math.ceil(totalCount / pagePerObjects);
  const [currentPage, setCurrentPage] = useState(1);

  const handlePreviousPage = () => {
    setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const handlePageClick = (pageNumber: number) => {
    setCurrentPage(pageNumber);
  };

  const startIdx = (currentPage - 1) * pagePerObjects;
  const endIdx = Math.min(startIdx + pagePerObjects, totalCount);
  const paginatedData = filteredPosts.slice(startIdx, endIdx);

  const handleClick = (data: Post) => {
    navigate(`/post/${data.pid}`);
  };

  return (
    <>
      <div>
        <S.CardList>
          {paginatedData.map((data) => (
            <S.StyledCard key={data.pid} onClick={() => handleClick(data)}>
              <p>제목: {data.title}</p>
              <p>카테고리: {data.category}</p>
              <p>가격: {data.price}</p>
            </S.StyledCard>
          ))}
        </S.CardList>
        <div>
          <StyledButton onClick={handlePreviousPage} disabled={currentPage === 1} selected={false}>
            ⬅
          </StyledButton>
          {Array.from({ length: totalPages }, (_, i) => i + 1).map((pageNumber) => (
            <StyledButton
              key={pageNumber}
              onClick={() => handlePageClick(pageNumber)}
              selected={pageNumber === currentPage}
              disabled={currentPage === pageNumber}
            >
              {pageNumber}
            </StyledButton>
          ))}
          <StyledButton onClick={handleNextPage} disabled={currentPage === totalPages} selected={false}>
            ➡
          </StyledButton>
        </div>
      </div>
    </>
  );
};

export default Pagination;

interface ButtonProps {
  selected: boolean;
}

const StyledButton = styled.button<ButtonProps>`
  margin: 4px;
  padding: 8px 16px;
  border: none;
  background-color: ${(props) => (props.selected ? 'yellow' : '#f0f0f0')};
  cursor: pointer;
  transition: background-color 0.2s;

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;
